import React, { Component } from "react";
import { Container, Row, Col, Image } from "react-bootstrap";

class Item extends Component {
  render() {
    const { titre, image, description, url } = this.props; //Ici on met les props, qui sont titre et image et qu'on récup chez le composant parent, qu'on a créer là bas
    return (
      <div>
        <Container align="right">
          <Row>
            <Col xs={6} md={10} align="center">
              {titre}
            </Col>
          </Row>
          <h4>{description}</h4>
          <Col xs={6} md={10}>
            <Image
              src={image}
              alt="image"
              style={{ width: 100, height: 130 }}
              rounded
            />
          </Col>
          <video src={url} />
        </Container>
      </div>
    );
  }
}
export default Item;
